public class _04_escapeSequences {

	/**
	 * @param args
	 */
	
	// 	escape sequences 
	//  \t: paragraph space
	//  \n: append new line
	// 	\"  print "
	//  \\ : prints \
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Make triangle\n---------------------------");
		System.out.println("\tI want to start my triangle from here");
		System.out.println("\t     ^");
		System.out.println("\t    / \\\\");
	}

}
